"use client";

import * as React from "react";
import { cn } from "@/lib/utils";

export interface StackedLogosProps {
    logoGroups: React.ReactNode[][];
    duration?: number;
    stagger?: number;
    logoWidth?: string;
    className?: string;
}

export const StackedLogos = ({
    logoGroups,
    duration = 30,
    stagger = 0,
    logoWidth = "200px",
    className,
}: StackedLogosProps) => {
    const itemCount = logoGroups[0]?.length || 0;
    const columns = logoGroups.length;

    return (
        <div
            className={cn("stacked-logos relative w-full", className)}
            style={{
                "--duration": duration,
                "--items": itemCount,
                "--lists": columns,
                "--stagger": stagger,
                "--logo-width": logoWidth,
            } as React.CSSProperties}
        >
            <div className="grid" style={{ gridTemplateColumns: `repeat(${columns}, ${logoWidth})` }}>
                {logoGroups.map((logos, groupIndex) => (
                    <div
                        key={groupIndex}
                        className="stacked-logos__cell relative grid transition-colors duration-200 hover:bg-zinc-100/60 dark:hover:bg-zinc-900/60"
                        style={{ "--index": groupIndex, gridTemplate: "1fr / 1fr" } as React.CSSProperties}
                    >
                        <div className="absolute top-0 bottom-0 right-0 w-px bg-zinc-200 dark:bg-zinc-800" />
                        <div className="absolute left-0 right-0 bottom-0 h-px bg-zinc-200 dark:bg-zinc-800" />
                        <div className="absolute left-0 right-0 top-0 h-px bg-zinc-200 dark:bg-zinc-800" />
                        {groupIndex === 0 && <div className="absolute top-0 bottom-0 left-0 w-px bg-zinc-200 dark:bg-zinc-800" />}
                        <div className="stacked-logos__accent-left absolute top-0 bottom-0 left-0 w-0.5 bg-amber-500 dark:bg-amber-400 opacity-0 transition-opacity duration-200" />
                        <div className="stacked-logos__accent-bottom absolute left-0 right-0 bottom-0 h-0.5 bg-amber-500 dark:bg-amber-400 opacity-0 transition-opacity duration-200" />
                        {logos.map((logo, logoIndex) => (
                            <div
                                key={logoIndex}
                                className="stacked-logos__item col-start-1 row-start-1 grid place-items-center py-16 px-8"
                                style={{ "--i": logoIndex } as React.CSSProperties}
                            >
                                <div className="stacked-logos__logo w-full h-8 flex items-center justify-center [&>svg]:h-full [&>svg]:w-auto [&>svg]:fill-zinc-700 dark:[&>svg]:fill-zinc-300 [&>img]:h-full [&>img]:w-auto [&>img]:object-contain [&>img]:grayscale [&>img]:brightness-50 dark:[&>img]:brightness-125">
                                    {logo}
                                </div>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

StackedLogos.displayName = "StackedLogos";
export default StackedLogos;
